    const dispatch = useDispatch();
console.log(cartItem)

    return(
   
        <div>
             <span> <img src={`./${dish.img}.jpg`} width="40px" alt="Bild"/></span> 
            <p>{dishes.name}</p>
           <p>{cartItem.quantity} Portionen</p> 
           <p>Preis: {dishes.price * cartItem.quantity} â‚¬</p>
        <span onClick={() => dispatch(removeItemFromCart({cartItemId: cartItem.id}))}><img className="delete" src="delete_3405244.png" alt="delete"/></span>

        </div>

    )
}

export default CartItem;
